"use strict";

$.get("../data/list.json?t=".concat(new Date().getTime()), function (data) {
  $('#goods_num').text(data.product.length);
  $.each(data.product, function (index, product) {
    $('main #img').append("\n        <a href=\"list.html\">\n            <div class=\"good\">\n                <img src=\"".concat(product.img, "\" alt=\"\">\n                <p class=\"p1\">").concat(product.name, "</p>\n                <h6 class=\"h6\">").concat(product.describe, "</h6>\n                <h5 class=\"h5\">").concat(product.price, "</h5>\n            </div>\n        </a>\n    "));

    if ((index + 1) % 3 === 0) {
      $('#img').eq(index).addClass('good1');
    }
  });
}); //aside
//滚动事件

$(window).scroll(function () {
  console.log($(document).height());
  console.log($(window).height());

  if ($('aside').offset().top >= 700) {
    $('aside').css('opacity', 1);
  } else {
    $('aside').css('opacity', 0);
  }

  $('aside #a1').click(function () {
    $(document).height() == 0;
  });
  console.log($(document).height());
});